sys_script_email.script:
(function executeRule(current, previous /*null when async*/ ) {
if (current.item_type == "cmdb_ci_computer") {
var request = new sn_ws.RESTMessageV2();
request.setEndpoint('https://adf-may-593-test-005.lab.service-now.com/api/now/table/u_password_vault?sysparm_query=u_asset%3D' + current.cmdb_ci.asset_tag + '&sysparm_fields=u_password&sysparm_limit=1');
request.setHttpMethod('GET');
//Eg. UserName="admin", Password="admin" for this code sample.
var user = 'admin';
var password = 'adf-may-593';
request.setBasicAuth(user, password);
request.setRequestHeader("Accept", "application/json");
var response = request.execute();
template.print(response.getBody());
var obj = JSON.parse(response.getBody());
var myPass = obj.result[0].u_password;
template.print('u_password:' + myPass);
}
})(current, previous);
